mapper.update:
UPDATE j_need
 SET hits = hits+1
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT n.id, n.title, n.content, n.member_id, n.company_id, n.attribute, n.status, n.class_id, n.add_time, n.update_time, n.hits, n.end_time, n.need_mobile, n.charge_type, c.name as className, m.name as memberName, m.head_portrait as headPortrait, m.mobile, m.address, a.full_name as areaFullName
,mvn.id as memberViewNeedId
FROM j_need n LEFT JOIN j_class c ON n.class_id = c.id LEFT JOIN j_member m on m.id = n.member_id LEFT JOIN j_area a on a.code = m.area_code
LEFT JOIN j_member_view_need mvn on mvn.need_id = n.id and mvn.member_id = #{memberId,jdbcType=BIGINT}
n.member_id = #{memberId,jdbcType=BIGINT}
AND n.status = #{status,jdbcType=TINYINT}
AND title like CONCAT('%',#{title},'%')
AND n.id = #{id}
AND m.area_code like CONCAT(#{areaCode},'%')
AND c.code like CONCAT(#{classCode},'%')
AND n.member_type = #{memberType}
order by add_time desc

SELECT n.id, n.title, n.content, n.member_id, n.company_id, n.attribute, n.status, n.class_id, n.add_time, n.update_time, n.hits, n.end_time, n.need_mobile, n.charge_type, c.name as className, m.name as memberName, m.mobile, m.address, a.name as attrName, a.value as attrValue, a.class_id as classId, a1.full_name as areaFullName FROM j_need n LEFT JOIN j_class c ON n.class_id = c.id LEFT JOIN j_member m on m.id = n.member_id LEFT JOIN j_need_attribute a on a.need_id = n.id LEFT JOIN j_area a1 on a1.code = m.area_code
member_id = #{memberId,jdbcType=BIGINT}
AND n.status = #{status,jdbcType=TINYINT}
AND title like CONCAT('%',#{title},'%')
AND n.id = #{id}
